<!DOCTYPE HTML><html lang="pt-br">	<head>		<title>Atividade 29/03</title>		<meta charset="UTF-8">		<link rel="stylesheet" type="text/css" href="stylesheet.css" />		<?php			include "conexao.php";			$email_edicao = $_GET['email'];						$sql = "SELECT * FROM usuario_tb WHERE email = '$email_edicao'";			$contatos = $conex -> prepare($sql);			$contatos -> execute();						foreach($contatos as $bolacha)			{				$id_edicao = $bolacha['idUsuario'];			}			$contatos = NULL;						if (is_null($id_edicao)){				header("location: notFound.php");			}						if(isset($_POST["enviar"]))				{										$email = $_POST["email_new"];										if ($_POST["senha_new"] != ""){						$senha = $_POST["senha_new"];						$senha_crypto = md5($senha);												include "conexao.php";						$sql = "UPDATE usuario_tb SET 								email = ?, 								senha = ?								WHERE idUsuario = ?";						$contatos = $conex -> prepare($sql);						$contatos -> execute(array($email, $senha_crypto, $id_edicao));						$contatos = NULL;					}										if ($_POST["senha_new"] == ""){						include "conexao.php";						$sql = "UPDATE usuario_tb SET 								email = ?								WHERE idUsuario = ?";						$contatos = $conex -> prepare($sql);						$contatos -> execute(array($email, $id_edicao));						$contatos = NULL;					}					header("location:listarContas-ADM.php");				}		?>	</head>	<body>		<header>			<hr>			<h2><center>Atividade 29/03 - Ediçao Contas - Gabriel Braz</center></h2>			<hr>		</header>				<a href="menuADM.php">			<input class="botaoTop" type="button" value="Menu Principal" name="index" align="center">		</a>				<div>			<fieldset>				<legend>					Dados para Edição do usuário nro. <?php echo $id_edicao;?>:				</legend>								<hr>								<form action="" method="POST">					<p>						Endereco de E-mail: <br>						<input type="text" name="email_new" value="<?php echo $email_edicao; ?>" maxlength="200" autofocus required>					</p>										<hr>										<p>						Senha:<br>						<input type="password" name="senha_new">					</p>															<hr>										<p>						<input type="submit" value="enviar" name="enviar">					</p>				<form>			</fieldset>					</div>	</body></html>